import React, { useEffect, useRef } from "react";

function UseRefHook() {
  const ref = useRef("");
  useEffect(() => {
    ref.current.focus();
  }, []);
  return (
    <div className="pt-16 flex justify-center">
      <input ref={ref} name="firstName" placeholder="First Name" />
    </div>
  );
}

export default UseRefHook;
