import React, { useState, useCallback } from "react";
import UseCallback from "./UseCallback";
import Count from "./Count";
import Button from "./Button";

function ParantComponent() {
  console.log("parent component");
  const [count, setCount] = useState(0);
  const [salary, setSalary] = useState(5000);

  const handleIncrementCount = useCallback(() => {
    setCount(count + 1);
  }, [count]);

  const handleIncreamentSalary =useCallback(()=>{
    setSalary(count + 1);
},[salary])
  return (
    <div className="text-center">
      <UseCallback />
      <Count count={count} text="Count" />
      <Button onClick={handleIncrementCount}>Count +</Button>
      <Count count={salary} text="Salary" />
      <Button onClick={handleIncreamentSalary}>Salary +</Button>
    </div>
  );
}

export default ParantComponent;
